package day3;

public class DataTypeDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x1 = 10;  // 10 is a integer ( x1 is a variable)
		Integer x2 = 100; // x2 is also a integer ( x2 is a reference of integer class - Wrapper class)
		
		System.out.println(x1);  //10
		System.out.println(x2);  //100
		
		//note : when you try x1. then you did not get any method and  x2. then you got list of method
		short y1 =7878;
		
		Short y2 = 7889;
		
	}

}
